import React, { Component } from 'react';
import { withHighcharts, HighchartsChart, Chart, XAxis, YAxis, Title, Legend, ScatterSeries, Tooltip } from 'react-jsx-highcharts';
import Highcharts from 'highcharts';
import addHighchartsMore from 'highcharts/highcharts-more';
import '../css/Chart.css'

addHighchartsMore(Highcharts);

class ScatterChart extends Component {
    constructor(props) {
        super(props);

        this.state = {
            data: [
                {
                    minTiming: 760,
                    '25thPercentile': 801,
                    medianTiming: 848,
                    '75thPercentile': 895,
                    maxTiming: 965
                }
            ]
        }
    }


    render() {

        const data = this.state.data[0];

        const tooltipFormatter = function (tooltip) {
            console.log(this.series.name);
            // const indexes = {};
            // this.points.forEach((point) => {
            //   indexes[point.series.name] = point.point.index;
            // });
            // console.log(indexes);
            // const self = tooltip.chart.options.chart.context;
            // self.props.onTooltipUpdate(indexes);

            return `<strong>${this.series.name}</strong><br />${this.y} (${this.point.location})<br><strong>elango</strong>`
          }

        return (
            <div>
                <HighchartsChart>
                    <Chart zoomType="xy" onClick={this.handleClick} />

                    <Title>Click to add data</Title>

                    <Legend>
                        <Legend.Title>Legend</Legend.Title>
                    </Legend>

                    <XAxis>
                        <XAxis.Title>X Coord</XAxis.Title>
                    </XAxis>

                    <YAxis>
                        <YAxis.Title>Y Coord</YAxis.Title>
                        <ScatterSeries name="Books" data={this.props.data.book} onHide={this.handleHide} onShow={this.handleShow}/>
                        <ScatterSeries name="Corpus" data={this.props.data.corpus} onHide={this.handleHide} onShow={this.handleShow}/>
                        <ScatterSeries name="Keyword" data={[this.props.data.keys]} onHide={this.handleHide} onShow={this.handleShow}/>
                    </YAxis>
                    <Tooltip padding={10} hideDelay={250} shape="square" split formatter={tooltipFormatter}/>
                </HighchartsChart>
            </div>
        );
    }
}
export default withHighcharts(ScatterChart, Highcharts); // Injecting the Highcharts object
